lemma = spacy.load('en_core_web_sm', disable=['parser', 'ner'])

posts1 = covid_first_months['Post Description'].values.tolist()
posts2 = covid_last_months['Post Description'].values.tolist()

preprocessed_posts1 = []
for lemmatized_post in lemma.pipe(covid_first_months['Post Description']):
    preprocessed_posts1.append(" ".join(word.lemma_ for word in lemmatized_post if word.lemma_ not in stop_words))

preprocessed_posts2 = []
