# train model on first months
model1 = Word2Vec(preprocessed_posts1, window=5, min_count=5, max_final_vocab=50000, vector_size=300, workers=1, seed=0)
word_vectors1 = model1.wv
model1.save("word2vec_covid_first_months_trained.model")
del model1

# train model on last months
model2 = Word2Vec(preprocessed_posts2, window=5, min_count=5, max_final_vocab=50000, vector_size=300, workers=1, seed=0)
word_vectors2 = model2.wv
model2.save("word2vec_covid_last_months_trained.model")
del model2

# compare terms and get the 10 most changed words

similar_words1 = {}
similar_words2 = {}

for word in vocabs:
    if word in word_vectors1.key_to_index:
        similar_words1[word] = [item[0] for item in word_vectors1.most_similar(positive=word, topn=50)]
    else:
        similar_words1[word] = []

    if word in word_vectors2.key_to_index:
        similar_words2[word] = [item[0] for item in word_vectors2.most_similar(positive=word, topn=50)]
    else:
        similar_words2[word] = []

common_words_count = {}
for word in vocabs:
    common_words_count[word] = len(set(similar_words1[word]).intersection(set(similar_words2[word])))


ten_most_changed = sorted(common_words_count, key=common_words_count.get)[:10]

